import React from "react";
import "../home/Home.css";
import { Modal, ModalBody} from "reactstrap"
import { useState } from "react";

export default function Home() {
    const [modal,setModal]=useState(false)

    return ( <>
        <div id = 'landing-con' >
            <div>
                <img src = { require('../../assets/Group 4.png').default}/> 
                <h1 > source of<br/> intelligence </h1> 
                <p> Sign - up and receive unlimited accesss to all of your literatur - share your literature. </p> 
                {modal && <SignIn closeModal={setModal}/>}
                <div>
                    <button  id='btn-signUp'>Sign Up</button>
                    <button onClick={()=>setModal(true)} id='btn-signIn'>Sign In</button>
                </div>
            </div>
            <img id="img-landing" src = {require('../../assets/g12.png').default }/> 
            
        </div> 

   
        </>
    )
    function SignIn({closeModal}){
        
        return(
            <div className='modalBackground'>
                <div className='modalContainer'>
                    <button onClick={()=>closeModal(false)}>X</button>
                    <div className='title'>Haloooo</div>
                    <div className='body'></div>
                    <div className='footer'></div>

                </div>
            </div>
        )
        }
    }
