import './index-owner.css'
 import { API } from '../../config/api'
   
export default function CardOwner({item}){
    const Status =()=>{
    if(item.status =='approve'){
        return(<>
        <td style={{flex:20,color:'green'}}>{item.status}</td>
        <td><img style={{width: 40,height:40}} src={require('../../assets/Vector (4).png').default}></img></td>
        </>)
    }else if(item.status =='cancel'){
        return(<>
        <td style={{flex:20,color:'red'}}>{item.status}</td>
        <td><img style={{width: 40,height:40}}src={require('../../assets/remove.png').default}></img></td>
         </>)
    }else{
        return(<>
        <td style={{flex:20,color:'#F7941E'}}>{item.status}</td> 
        <td style={{flex:20}}>
                    <button style={{
                            width: 100,
                            height: 35,
                            color: '#161616',
                            backgroundColor: '#FF0742',
                            borderRadius: 5,
                            fontWeight: 'bold'
                    }} onClick ={cancel} id="btn-cancel">Cancel</button>
                    <button style={{
                            marginLeft: 10,
                            width: 100,
                            height: 35,
                            color: '#161616',
                            backgroundColor: '#0ACF83',
                            borderRadius: 5,
                            fontWeight: 'bold'
                    }} onClick={approve} id="btn-approve">Approve</button>
                </td>
        </>)
    }
}

const approve = async (e)=>{
    e.preventDefault();
    const config ={
      headers:{
        "Content-Type": "application/json",
      },
    };
    const data =({"status":"Approve"})
    const update = await API.put(`/literature/${item.id}`,data, config);
    console.log(update)
}

const cancel = async (e)=>{
    e.preventDefault();
    const config ={
      headers:{
        "Content-Type": "application/json",
      },
    };
    const data =({"status":"Cancel"})
    const update = await API.put(`/literature/${item.id}`,data, config);
    console.log(update)

}
    return(
       <>
            <tr id="item" style={{textAlign:'left',height:70,paddingTop:50}}>
                <td style={{flex:10}}>1</td>
                <td style={{flex:20}}>{item.author}</td>
                <td style={{flex:20}}>{item.isbn}</td>
                <td style={{flex:20}}>{item.title}</td>
                <Status/>
                
            </tr>
       </>
    )
}
