import './index-owner.css'
 import { API } from '../../config/api'
   
export default function CardOwner({item}){
    const Status =()=>{
    if(item.status =='Approve'){
        return(<>
        <label style={{width:200,height:73,marginLeft:140,color:'#0ACF83'}}>{item.status}</label>
        <label><img style={{marginLeft:170,width: 40,height:40}} src={require('../../assets/Group.png').default}></img></label>
        </>)
    }else if(item.status =='Cancel'){
        return(<>
        <label style={{width:200,height:73,marginLeft:140,color:'red'}}>{item.status}</label>
        <label><img style={{marginLeft:175,width: 40,height:40,}}src={require('../../assets/remove.png').default}></img></label>
         </>)
    }else{
        return(<>
        <label style={{marginLeft:130,color:'#F7941E'}}>{item.status}</label> 
        <label style={{marginLeft:1090}}>
                    <button style={{
                            width: 100,
                            height: 35,
                            color: '#161616',
                            backgroundColor: '#FF0742',
                            borderRadius: 5,
                            fontWeight: 'bold'
                    }} onClick ={cancel} id="btn-cancel">Cancel</button>
                    <button style={{
                            marginLeft: 10,
                            width: 100,
                            height: 35,
                            color: '#161616',
                            backgroundColor: '#0ACF83',
                            borderRadius: 5,
                            fontWeight: 'bold'
                    }} onClick={approve} id="btn-approve">Approve</button>
                </label>
        </>)
    }
}

const approve = async (e)=>{
    e.preventDefault();
    const config ={
      headers:{
        "Content-Type": "application/json",
      },
    };
    const data =({"status":"Approve"})
    const update = await API.put(`/literature/${item.id}`,data, config);
    console.log(update)
    window.location.reload()
   
}

const cancel = async (e)=>{
    e.preventDefault();
    const config ={
      headers:{
        "Content-Type": "application/json",
      },
    };
    const data =({"status":"Cancel"})
    const update = await API.put(`/literature/${item.id}`,data, config);
    console.log(update)
    window.location.reload()
 
}
    return(
       <>
            <div id="item">
                <label >1</label>
                <label >{item.author}</label>
                <label >{item.isbn}</label>
                <label >{item.title}</label>
                <Status/> 
            </div>
       </>
    )
}
