import './detail-literature.css'
import Navbar from '../../components/navbar'
import Literature from '../../dataLiterature.json'
import { useState,useEffect } from 'react';
import { useParams } from 'react-router-dom';

export default function DetailLiterature(){
    let { id } = useParams();
    const [literature,setLiterature] = useState([])
    const getLiterature = async (id) => {
        try {
          const response = await API.get(`/literature/${id}`);
          setLiterature(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
      useEffect(() => {
        getLiterature(id);
        
      }, []);
    return(
        <>
        <Navbar/>
        <div id="detail-literature">
            <img/>
            <div id="literature">
                <h1 style={{color:'white', fontSize:36,}}>{Literature[0].title}</h1>
                <h3 style={{color:'#929292', fontSize:24,}}>{Literature[0].author}</h3>
                <h2 style={{color:'white', fontSize:24,}}>Publication Date</h2>
                <h3 style={{color:'#929292', fontSize:18,}}>{Literature[0].publication_date}</h3>
                <h2 style={{color:'white', fontSize:24,}}>Pages</h2>
                <h3 style={{color:'#929292', fontSize:18,}}>{Literature[0].pages}</h3>
                <h2 style={{color:'#AF2E1C', fontSize:24,}}>ISBN</h2>
                <h3 style={{color:'#929292', fontSize:18,}}>{Literature[0].isbn}</h3>
                <button>Download</button>
            </div>
            <div id='btn-literature'>
                <button>Add My Collection</button>
            </div>
            
        </div>
        </>
    )
}
