import Navbar from '../../components/navbar'
import './index-owner.css'
import CardOwner from './card-owner'
import { Table } from 'reactstrap'
import { useState } from 'react'
import { API } from '../../config/api'
import { useEffect } from 'react'
export default function IndexOwner(){
    const [status, setStatus]=useState([])
    const getStatus = async () => {
        try {
          const response = await API.get(`/literatures`);
          setStatus(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
    
      useEffect(() => {
        getStatus();
      }, []);


    return(
        <>
        <Navbar/>
        <div id="index-owner">
            <h1>Book Verification</h1>
            <div id="owner-container">
                <thead>
                    <tr>
                    <th style={{flex:20}}>No.</th>
                    <th style={{flex:20}}>Users of Authors</th>
                    <th style={{flex:20}}>ISBN</th>
                    <th style={{flex:20}}>Literature</th>
                    <th style={{flex:20}}>Status</th>
                    <th style={{flex:20}}>Action</th>
                    </tr>
                </thead>
                <tbody>
                {status.map((item) => (<CardOwner item={item} />))}
                </tbody> 
             
            </div>
        </div>
        </>
    )
}
