    const getLiterature = async () => {
        try {
        
          const response = await API.get(`/literature-search/${search}`);
          setuser(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
    
    console.log(search)
    return(
        <>
            <Navbar/>
            <button id="btn-search"><img src={ require('../../assets/Vector (2).png').default}/></button>
            <div id="search-container">
                <img src = { require('../../assets/Vector (1).png').default}/> <br/>
