import './profile.css'
import Navbar from '../../components/navbar'
import { useParams } from 'react-router'
import { useEffect,useState } from 'react'
import Card from '../../components/card'
import {API} from '../../config/api'
export default function Profile(){
    
    const [user,setuser]=useState([])
    const id = sessionStorage.getItem('user')

    const getuser = async () => {
        try {
        
          const response = await API.get(`/user/${id}`);
          setuser(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
    
      useEffect(() => {
        getuser();
        
      }, []);

      const handleClick = async (e)=>{
        e.preventDefault();
        const config ={
          headers:{
            "Content-type": "multipart/form-data",
          },
        };
        const formData = new FormData();
      if (form.attachment) {
        formData.set("attachment", form?.attachment[0], form?.attachment[0]?.name);
      }
  
        
        const update = await API.put(`/user/${id}`,formData, config);
        console.log(update)
        handleShow()
    }

    return(
        <>
        <Navbar/>
        <div>
            <h1 id="title-profile">Profile</h1>
            <div id="profile-con">
                <div id="main-profile">
                    <div>
                        <img src={require('../../assets/Vector1.png').default}/>
                        <div>
                            <label id="label">{user.email}</label><br/>
                            <p>Email</p><br/>
                        </div>
                    </div>
                    <div>
                        <img src={require('../../assets/Vector (3).png').default}/>
                        <div>
                            <label id="label">{user.gender}</label><br/>
                            <p>Gender</p><br/>
                        </div>
                    </div>
                    <div>
                        <img src={require('../../assets/Vector.png').default}/>
                        <div>
                            <label id="label">{user.phone}</label><br/>
                            <p>Mobile Phone</p><br/>
                        </div>
                    </div>
                    <div>
                        <img src={require('../../assets/Vector2.png').default}/>
                        <div>
                            <label id="label">{user.address}</label><br/>
                            <p>Address</p><br/>
                        </div>
                    </div>
                </div>
                <form id="image-profile">
                    <img></img>
                    <button type="submit" id="btn-profile">Change Photo Profile</button>
                </form>
            </div>
            <h1 id="my-literature">My Literature</h1>
            <div id="profile-list">
          
            </div>
        </div>
        </>
    )
}
