import './search-result.css'
import Navbar from '../../components/navbar'
import Card from '../../components/card'
import { useState,useEffect } from 'react'
import {API} from '../../config/api'


export default function SearchResult(){

    const [literature,setLiterature]= useState([])
    const [input,setInput] = useState()
    const search =sessionStorage.getItem('search')
    sessionStorage.setItem('search',input)
    const getLiterature = async () => {
        try {
        
          const response = await API.get(`/literatures`);
          setLiterature(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
      console.log(literature)
      useEffect(() => {
          getLiterature()
        
      }, [])
      console.log(search)
      const Maps =()=>{
           if(search == 'undefined'){
              return(<>
                {literature.map((item) => (<Card item={item} />))}
                </>
              )
          } else if((item) => item.title == search){
            return(<>
              {literature.filter((item) => item.title == search).map((item) => (<Card item={item} />))}
            </>)
            }else{
                 return(<>
                   {literature.map((item) => (<Card item={item} />))}
                    </>
                )
            }
      }
      const Approve=()=>{
        if(literature.status == 'Approve'){
          return(<>
            <Maps/></>
          )
        }else{
          return(<></>)
        }
      }

      return(
        <>
        <Navbar/>
        <div id="search-result">
            <div>
                <input onChange ={(e)=>setInput(e.target.value)}></input>
                <button><img src={ require('../../assets/Vector (2).png').default}/> </button>
            </div>
            <div id="result">
                <div id="result-left">
                    <label>Anytime</label><br/>
                    <select>
                        <option>Since 2020</option>
                        <option>Since 2019</option>
                        <option>Since 2018</option>
                        <option>Since 2017</option>
                    </select>
                </div>
                <div id="result-right">
                    
                </div>
            </div>
        </div>
        </>
    )
}
