import './add-literature.css'
import Navbar from '../../components/navbar'
import { useState } from 'react'

export default function AddLiterature(){
    const [form,setForm]=useState({
        title:'',
        publication_date:'',
        pages:'',
        isbn:'',
        author:'',
        image:''
    })
    const handleChange=()=>{
        setForm({
            ...form,
            [e.target.name]: e.target.type === "file" ? e.target.files : e.target.value,
        });
      
        // Create image url for preview
        if (e.target.type === "file") {
      
          let url = URL.createObjectURL(e.target.files[0]);
          setPreview(url);
        }
    }


    return(
        <>
            <Navbar/>
            <div>
                <h1 id="title-addLiterature">Add Literature</h1>
                <form id="addLiterature">
                <input name="title" placeholder="Title" type="text"></input><br/>
                <input name="publication_date" placeholder="Publication Date"></input><br/>
                <input name="pages" placeholder="Pages" type="Number"></input><br/>
                <input name="isbn" placeholder="ISBN" type="text"></input><br/>
                <input name="author" placeholder="Author, Ex : E E Rizky, Astina Haris" type="text"></input><br/>
                <div id="input-file">
                    <label for="input">Attache book file <img src={require('../../assets/Frame 1.png').default}/></label>
                    <input id="input" type="file" hidden></input><br/>
                </div>
                <button type="submit" id="btn-addLiterature">Add Literature</button>
                </form>
            </div>
        </>
    )
}
